$(document).ready(function () {
  $(".hashpower-tabs").hide();
  $("ul.hashpower-duration li:first").addClass("current").show();
  $(".hashpower-tabs:first").show();
  $("ul.hashpower-duration li").click(function () {
    $("ul.hashpower-duration li").removeClass("current");
    $(this).addClass("current");
    $(".hashpower-tabs").hide();
    var activeTab = $(this).find("a").attr("href");
    $(activeTab).show();
    return false;
  });
});
